// attempt to copy http://www.polyducks.com/assets/the-tower.png

DrawBackground: {
	Set $d020:#BLUE
    Set $d021:#BLUE

    // this code generated by http://petscii.krissz.hu/
    // screen and color pointer low bytes
	lda #$00
	sta $fd
	sta $f7
    // screen pointer high byte $0400
	lda #$04
	sta $fe
    // colorpointer high byte $d800
	lda #$d8
	sta $f8

	lda #<BackgroundCharacterData
	sta $fb
	lda #>BackgroundCharacterData
	sta $fc

	lda #<BackgroundColorData
	sta $f9
	lda #>BackgroundColorData
	sta $fa

	ldx #$00
	ldy #$00
	lda ($fb),y
	sta ($fd),y
	lda ($f9),y
	sta ($f7),y
	iny
	bne *-9

	inc $fc
	inc $fe
	inc $fa
	inc $f8

	inx
	cpx #$04
	bne *-24

    rts
}

